let data={sketch:{background:"#0000000A"}},nx=0,ny=0,nz=0;function setup(){createCanvas(windowWidth,windowHeight),background(255),colorMode(HSB,100)}function draw(){background(data.sketch.background),drawStream()}function drawStream(){let a=0;for(let c,b=0;b<width;b+=20){c=0;for(let d=0;d<width;d+=20){let e=map(noise(a,c,nz),0,1,0,4*PI),f=30*cos(e),g=30*sin(e);stroke((frameCount+50*noise(a+c+nz))%100,map(e,0,4*PI,70,100),100),line(b,d,b+f,d+g),c+=0.03}a+=0.02}nz+=0.01}