let data={sketch:{background:"#000000"}};class Eye{constructor(a,b,c){this.x=a,this.y=b,this.r=c,this.angle=0}update(a,b){this.angle=atan2(b-this.y,a-this.x)}show(){push(),translate(this.x,this.y),fill(255),ellipse(0,0,this.r,this.r),rotate(this.angle),fill(153,204,0),ellipse(this.r/4,0,this.r/2,this.r/2),pop()}}let eyes=[];function setup(){createCanvas(windowWidth,windowHeight);for(var a=0;a<14;a+=1)eyes.push(new Eye(random(0,width),random(0,height),random(40,100)))}function draw(){background(data.sketch.background);for(var a=0,b=eyes.length;a<b;a+=1)eyes[a].update(mouseX,mouseY),eyes[a].show()}