var data={radius:10,limit:500},balls=[];class Ball{constructor(a=random(0,window.innerWidth),b=random(0,window.innerHeight),c=10){this.position=[a,b],this.velocity=[0,0],this.acceleration=[0,1],this.r=map(c,0,window.innerWidth+window.innerHeight,1,100)}display(){strokeWeight(1),stroke(200),fill(50),ellipse(this.position[0],this.position[1],this.r,this.r)}move(){this.position[0]+=this.velocity[0],this.position[1]+=this.velocity[1],this.velocity[0]+=this.acceleration[0],this.velocity[1]+=this.acceleration[1],(this.position[0]>=width-0||0>=this.position[0])&&(this.velocity[0]*=-1),(this.position[1]>=height||0>=this.position[1])&&(this.velocity[1]=-1*this.velocity[1])}}function setup(){createCanvas(windowWidth,windowHeight)}function draw(){background(0);for(var a=0;a<balls.length;a+=1)balls[a].display(),balls[a].move(),balls.length>data.limit&&balls.splice(0,1)}function mouseDragged(){balls.push(new Ball(mouseX,mouseY,data.radius))}