let data={sketch:{background:"#FFFFFF"},bodies:{n:70,spacing:20,base_radius:0,colored:!1}},bodies=[];function setup(){createCanvas(windowWidth,windowHeight);for(var a=1,b=data.bodies.n;a<=b;a+=1)bodies.push(new Body(0.5*width,0.5*height,a));noStroke(),noFill(),ellipseMode(CENTER),rectMode(CENTER),colorMode(HSB,100)}function draw(){background(data.sketch.background);for(var a=bodies.length-1;a>0;a-=1){if(data.bodies.colored){map(a,bodies.length-1,0,0,100);fill(map(a,0,100,0,100),map(a,0,100,100,0),map(a,0,100,100,0),map(a,0,100,50,0))}else a%2?fill(255):fill(0);bodies[a].show(),bodies[a].update()}}class Body{constructor(a,b,c=1){this.x=a,this.y=b,this.i=c,this.r=this.i*data.bodies.spacing+data.bodies.base_radius}show(){push(),rect(this.x,this.y,this.r,this.r),pop()}update(){this.x=100*sin(2e-3*(frameCount*(data.bodies.n-this.i)))+0.5*width}}function mousePressed(){data.bodies.colored=!data.bodies.colored}