let data={sketch:{background:"#000000",G:6.67408*Math.pow(10,0)},particle:{number:200,mass:1,size:3,stroke:{check:!1,color:"#000000",weight:3},fill:{check:!0,color:"#FFFFFF"}},attractor:{number:0,mass:10,size:10,stroke:{check:!1,color:"#000000",weight:3},fill:{check:!0,color:"#DE3030"}}},attractor1,attractor2,particles=[];function setup(){createCanvas(windowWidth,windowHeight),attractor1=new Attractor(0.35*width,0.5*height),attractor2=new Attractor(0.65*width,0.5*height),angleMode(DEGREES),colorMode(HSB,100);for(var a=0,b=data.particle.number;a<b;a+=1){let c=360/data.particle.number*a,d=0.5*width+0.23*width*cos(c),e=0.5*height+0.23*width*sin(c);particles.push(new Particle(d,e))}background(0)}function draw(){colorMode(HSB,100),background(0,20),particles.forEach(a=>{a.clearForce(),a.attracted(attractor1),a.attracted(attractor2),a.update(),a.edges(),a.show()}),attractor1.show(),attractor2.show()}