let data={sketch:{background:"#FFFFFF",max_clicks:2,counter:0},fractal:{root:300}},root,sponge=[];function setup(){createCanvas(windowWidth,windowHeight,WEBGL),root=new Box(0,0,0,data.fractal.root),sponge.push(root),colorMode(HSB,100)}function draw(){background(data.sketch.background),rotateX(0.01*frameCount),rotateY(0.01*frameCount);for(var a=0,c=sponge.length;a<c;a+=1)sponge[a].show()}function mousePressed(){if(data.sketch.counter<data.sketch.max_clicks){let a=[];sponge.forEach(c=>{a.push(...c.generate())}),sponge=a}data.sketch.counter++}