data={background:"#E8DADA",n:120,follow_mouse:!1,square:{stroke:"#000000",stroke_weight:1,fill:"#FFFFFF",rotation_speed:2.1,size:70},movement:{radius:220,revolution_speed:0,separation:0}};var controlKit,createControlKit=()=>{controlKit=new ControlKit,controlKit.addPanel({fixed:!1,label:"Controls"}).addColor(data,"background",{label:"Background"}).addCheckbox(data,"follow_mouse",{label:"Center according to Mouse?"}).addSubGroup({label:"Square Controls"}).addNumberInput(data.square,"rotation_speed",{label:"Rotation Speed",step:0.1}).addNumberInput(data.square,"size",{label:"Size"}).addNumberInput(data.square,"stroke_weight",{label:"Border Thickness"}).addColor(data.square,"stroke",{label:"Border Color"}).addColor(data.square,"fill",{label:"Fill Color"}).addNumberInput(data.movement,"radius",{label:"Revolution Radius",step:1}).addNumberInput(data.movement,"revolution_speed",{label:"Revolution Speed",step:0.1}).addNumberInput(data.movement,"separation",{label:"Separation",step:0.01,dp:3})};createControlKit();let squares=[];function setup(){createCanvas(windowWidth,windowHeight);for(var a=1;a<=data.n;a+=1)squares.push(new Square(width/2,height/2,data.square.size,a))}function draw(){if(background(data.background),!0==data.follow_mouse)for(var a=0,b=data.n;a<b;a+=1)squares[a].revCx=mouseX,squares[a].revCy=mouseY,squares[a].update(),squares[a].revolve(),squares[a].rotate(),squares[a].show();else for(var a=0,b=data.n;a<b;a+=1)squares[a].revCx=width/2,squares[a].revCy=height/2,squares[a].update(),squares[a].revolve(),squares[a].rotate(),squares[a].show()}