let data={sketch:{background:"#BC1E00"},squiggly:{n:50,np:50,separation:18,amplitude:20,inner_radius:30}},things=[];function setup(){createCanvas(windowWidth,windowHeight),angleMode(DEGREES),noFill(),stroke(255),strokeWeight(1);for(var a=0,b=data.squiggly.n;a<b;a+=1)things[a]=new Squiggly(0.5*width,0.5*height,a)}function draw(){background(188,30,0,100);for(var a=0,b=data.squiggly.n;a<b;a+=1)things[a].show();let c=2*(data.squiggly.inner_radius+data.squiggly.separation);push(),noStroke(),fill(255,100),translate(0.5*width,0.5*height),ellipse(0,0,c,c),pop()}class Squiggly{constructor(a,b,c=0){this.position=new p5.Vector(a,b),this.i=c+1,this.rotation=360/data.squiggly.n*(c+1)}show(){push(),translate(this.position.x,this.position.y),rotate(this.rotation),beginShape();for(var a=0,b=data.squiggly.np;a<b;a+=1){let c=data.squiggly.inner_radius+data.squiggly.separation*a,d=data.squiggly.amplitude*Math.log(a)*cos(5*frameCount+(a+1)*data.squiggly.amplitude);vertex(c,d)}endShape(),pop()}}